-- create "validator_telegram_chats" table
CREATE TABLE "validator_telegram_chats" ("validator_id" bigint NOT NULL, "telegram_chat_id" bigint NOT NULL, PRIMARY KEY ("validator_id", "telegram_chat_id"), CONSTRAINT "validator_telegram_chats_telegram_chat_id" FOREIGN KEY ("telegram_chat_id") REFERENCES "telegram_chats" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "validator_telegram_chats_validator_id" FOREIGN KEY ("validator_id") REFERENCES "validators" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
-- create "validator_discord_channels" table
CREATE TABLE "validator_discord_channels" ("validator_id" bigint NOT NULL, "discord_channel_id" bigint NOT NULL, PRIMARY KEY ("validator_id", "discord_channel_id"), CONSTRAINT "validator_discord_channels_discord_channel_id" FOREIGN KEY ("discord_channel_id") REFERENCES "discord_channels" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "validator_discord_channels_validator_id" FOREIGN KEY ("validator_id") REFERENCES "validators" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
